          </div>
        </div>
        <div class="text-white font-medium mb-5" style="font-size: 30px;line-height: 36px;">
          Đăng ký ROAD AI để được hưởng ưu đãi
        </div>
        <div class="text-white" v-if="$resources.info_package_free.data">
          <div class="flex mb-3 items-center">
            <svg width="32" height="33" viewBox="0 0 32 33" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path fill-rule="evenodd" clip-rule="evenodd" d="M16 28.8467C22.6274 28.8467 28 23.4741 28 16.8467C28 10.2193 22.6274 4.84668 16 4.84668C9.37258 4.84668 4 10.2193 4 16.8467C4 23.4741 9.37258 28.8467 16 28.8467ZM22.2763 15.1228C22.797 14.6021 22.797 13.7579 22.2763 13.2372C21.7556 12.7165 20.9114 12.7165 20.3907 13.2372L14.6668 18.9611L11.6096 15.9039C11.0889 15.3832 10.2447 15.3832 9.72402 15.9039C9.20332 16.4246 9.20332 17.2688 9.72402 17.7895L13.724 21.7895C14.2447 22.3102 15.0889 22.3102 15.6096 21.7895L22.2763 15.1228Z" fill="white"/>
            </svg>
            <span class="ml-10 font-normal" style="font-size: 22px;line-height: 22px;">Dung lượng lưu trữ miễn phí {{$resources.info_package_free.data.storage_volumn}}GB</span>
          </div>
          <div class="flex items-center">
            <svg width="32" height="33" viewBox="0 0 32 33" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path fill-rule="evenodd" clip-rule="evenodd" d="M16 28.8467C22.6274 28.8467 28 23.4741 28 16.8467C28 10.2193 22.6274 4.84668 16 4.84668C9.37258 4.84668 4 10.2193 4 16.8467C4 23.4741 9.37258 28.8467 16 28.8467ZM22.2763 15.1228C22.797 14.6021 22.797 13.7579 22.2763 13.2372C21.7556 12.7165 20.9114 12.7165 20.3907 13.2372L14.6668 18.9611L11.6096 15.9039C11.0889 15.3832 10.2447 15.3832 9.72402 15.9039C9.20332 16.4246 9.20332 17.2688 9.72402 17.7895L13.724 21.7895C14.2447 22.3102 15.0889 22.3102 15.6096 21.7895L22.2763 15.1228Z" fill="white"/>
            </svg>
            <span class="ml-10 font-normal" style="font-size: 22px;line-height: 22px;">{{$resources.info_package_free.data.pupv}} PUPV(Đơn vị phân tích video)</span>
          </div>
        </div>
      </div>
    </div>
    <div class="w-2/5 flex items-center justify-center h-full">
      <LoginBox v-if="!request_status_ok" title="Register an account" :class="{ 'pointer-events-none': loading }" class="top-[15%] absolute">
        <form class="flex flex-col" @submit.prevent="signup()">
          <Input v-model="fullName" class="mb-2" :label="__('Full Name')" type="text" autocomplete="full_name" required />
          <Input v-model="email" class="mb-2" label="Email" type="email" autocomplete="email" required />
          <Input v-model="pwd" :label="__('Password')" type="password" required />
          <ErrorMessage class="mt-4" :message="errorMessage" />
          <Button class="mt-4 focus:ring-0 focus:ring-offset-0" :loading="loading" variant="solid">
            {{ __('Submit') }}
          </Button>
          <div class="mt-10 text-center border-t">
            <div class="transform -translate-y-1/2">
              <span class="px-2 text-xs leading-8 tracking-wider text-gray-800 bg-white">
                {{ __('OR') }}
              </span>
            </div>
          </div>
          <div class="flex justify-center items-center">
            <span>{{ __('Already have an account?') }}</span>
            <span>&nbsp;&nbsp;</span>
            <router-link class="text-base text-blue-500" to="/login">
              {{ __('LOG IN') }}
            </router-link>
          </div>
        </form>
      </LoginBox>
      <div v-else class="p-5 sm:p-20 top-[15%] absolute">
        <div
          class="flex flex-col flex-1 items-center p-10 text-base bg-white rounded-lg mx-auto shadow-lg w-full sm:w-96">
          <div class="w-8 h-8 p-1 rounded-full" :class="iconContainerClass">
            <FeatherIcon :name="icon" :class="iconClass" />
          </div>
          <h2 class="mt-4 text-lg font-medium text-center text-gray-900">
            {{ response.title }}
          </h2>
          <p class="text-base text-center text-gray-700 mt-2">
            <!-- eslint-disable-next-line vue/no-v-html -->
            <span v-html="response.message" />
          </p>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import { Input, ErrorMessage, FeatherIcon } from "frappe-ui"
import LoginBox from "@/components/LoginBox.vue"

export default {
  name: "Signup",
  components: {
    Input,
    ErrorMessage,
    FeatherIcon,
    LoginBox,
  },
  data() {
    return {
      email: null,
      fullName: null,
      pwd: null,
      errorMessage: null,
      loading: false,
      request_status_ok: false,
      response: {
        title: null,
        message: null,
        color: null,
      },
    }
  },
  resources: {
    info_package_free() {
      return {
        url: "drive.api.service_package.package_free",
        method: "GET",
        auto: true,
      }
    }
  },
  computed: {
    iconClass() {
      return {
        red: "text-red-500",
        green: "text-green-500",
      }[this.response.color]
    },
    iconContainerClass() {
      return {
        red: "bg-red-100",
        green: "bg-green-100",
      }[this.response.color]
    },
    icon() {
      return {
        red: "x",
        green: "check",
      }[this.response.color]
    },
  },
  methods: {
    async signup() {
      try {
        this.errorMessage = null
        this.loading = true
        if (this.email && this.fullName && this.pwd) {
          let res = await this.$call("drive.api.user.sign_up", {
            full_name: this.fullName,
            email: this.email,
            redirect_to: "drive",
            pwd: this.pwd
          })
          if (res) {
            let [code] = res
            if (code > 0) this.request_status_ok = true
            if (code === 0) {
              this.errorMessage = __("This account already exists")
            } else if (code === 1) {
              this.response = {
                title: __("Register Account Successfully"),
                message: `${__('Your account was created successfully. Please activate account and')} <a href="/drive" class="text-base text-blue-500">${__('LOGIN IN')}</a> ${__('to use the service')}.`,
                color: "green",
              }
            } else {
              this.request_status_ok = true
              this.response = {
                title: __("Verification Needed"),
                message: __('Verification email was not sent. Please ask your administrator to verify your sign-up'),
                color: "red",
              }
            }
          }
        }
      } catch (error) {
        this.errorMessage = error.messages.join("\n")
      } finally {
        this.loading = false
      }
    },
  },
}
</script>

<style scoped>
.bg-signup{
  background: linear-gradient(135deg, #118D57 0%, #1AAC5B 61.5%, #1DB45C 87.5%, #22C55E 100%);
}
.bg-signup svg {
  position: absolute;
}
.container-left {
  position: absolute;
  top: 25%;
  left: 100px;
}
</style>
