<script>
import { Input, ErrorMessage, FeatherIcon } from "frappe-ui"
import LoginBox from "@/components/LoginBox.vue"

export default {
  name: "Signup",
  components: {
    Input,
    ErrorMessage,
    FeatherIcon,
    LoginBox,
  },
  data() {
    return {
      email: null,
      fullName: null,
      pwd: null,
      errorMessage: null,
      loading: false,
      request_status_ok: false,
      response: {
        title: null,
        message: null,
        color: null,
      },
      windowWidth: window.innerWidth
    }
  },
  resources: {
    info_package_free() {
      return {
        url: "drive.api.service_package.package_free",
        method: "GET",
        auto: true,
      }
    }
  },
  computed: {
    iconClass() {
      return {
        red: "text-red-500",
        green: "text-green-500",
      }[this.response.color]
    },
    iconContainerClass() {
      return {
        red: "bg-red-100",
        green: "bg-green-100",
      }[this.response.color]
    },
    icon() {
      return {
        red: "x",
        green: "check",
      }[this.response.color]
    },
    isTabletOrDesktop() {
      return this.windowWidth >= 768; // Tablet v Desktop c kch thc ln hn hoc bng 768px
    }
  },
  methods: {
    async signup() {
      try {
        this.errorMessage = null
        this.loading = true
        if (this.email && this.fullName && this.pwd) {
          let res = await this.$call("drive.api.user.sign_up", {
            full_name: this.fullName,
            email: this.email,
            redirect_to: "drive",
            pwd: this.pwd
          })
          if (res) {
            let [code] = res
            if (code > 0) this.request_status_ok = true
            if (code === 0) {
              this.errorMessage = __("This account already exists")
            } else if (code === 1) {
              this.response = {
                title: __("Register Account Successfully"),
                message: `${__('Your account was created successfully. Please activate account and')} <a href="/drive" class="text-base text-blue-500">${__('LOGIN IN')}</a> ${__('to use the service')}.`,
                color: "green",
              }
            } else {
              this.request_status_ok = true
              this.response = {
                title: __("Verification Needed"),
                message: __('Verification email was not sent. Please ask your administrator to verify your sign-up'),
                color: "red",
              }
            }
          }
        }
      } catch (error) {
        this.errorMessage = error.messages.join("\n")
      } finally {
        this.loading = false
      }
    },
    handleResize() {
      this.windowWidth = window.innerWidth;
    }
  },
  mounted() {
    window.addEventListener('resize', this.handleResize);
  },
  beforeDestroy() {
    window.removeEventListener('resize', this.handleResize);
  }
}
</script>

